 .pwi-print-tbl{
     border-collapse: collapse;
     width: 100%;
    /*margin-top: 170px;
    */
     table-layout: fixed;
}
 .pwi-print-tbl thead tr th{
     font-weight: 500;
     font-size: 10px;
     text-align: center;
     background-color: #EEEEEE;
}
 .pwi-print-tbl thead tr th:first-child{
     text-align: left;
}
 .pwi-print-tbl tbody tr td{
     font-weight: 400;
     font-size: 10px;
}
 .pwi-print-tbl thead tr th, .pwi-print-tbl tbody tr td{
     padding: 10px;
     text-align: center;
     border: 1px solid #D1D8DD;
}
 .pwi-print-tbl .detail-list-txt tr td, .pwi-print-tbl thead tr th:not(:first-child), .pwi-print-tbl .total-detail-txt{
     text-align: left;
}
 .pwi-print-tbl .total-detail-txt tr td:first-child{
     font-weight: 900;
     font-size: 12px;
}
 .pwi-print-tbl .total-detail-txt tr td{
     border: 0;
}
 .print-format, .print-format-preview {
    font-size: 10;
    padding: 0.25in;
        padding-top: 400px;
    page-break-after: always !important;


}
 .print-display-flex {
    /*display: flex;
    */
    /*justify-content: space-between;
    */
}
 .print-display-flex h2 div {
    font-size: 16px;
     font-weight: bold;
}
 .print-company-info {
    text-align: left;
}
 .print-company-info h3 {
    margin-top: 0;
     color: #0b6cb4;
}
 .print-company-info p {
     color: #0b6cb4;
     font-size: 11px;
     margin-bottom: 5px;
}
 #hp {
     float: right;
     margin: 0 0 0 15px;
}
 .table-sr {
     width: 5%;
}
/*New CSS 04/04/2023*/
